import React from 'react';
import "../Style/AddTask.scss";
import { Task } from '../types/Task';

interface IState {
  task: Task;
};

interface IProps {
}

class AddTask extends React.Component<IProps, IState> {

  constructor(props: IProps) {
    super(props);
    this.state = {
      task: {
        TaskContent: "chagit task"
      } as Task
    }
  };

  handleSubmit = async (e: any) => {
    console.log("form sybmitted")
  }


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
      <div className="card">
        <div className="card-header">
          <h1> Add your task</h1>
        </div>
        <div className="card-body">
          <div className="form-group">
            <label>Task</label>
            <div className="input-group mb-3">
              <input type="text" className="form-control" placeholder="Task" />
            </div>
          </div>
          <div className="form-group">
            <label>Image</label>
            <div className="input-group mb-3">
              <input type="text" className="form-control" placeholder="Image" />
              <div className="input-group-append">
                <span className="input-group-text" id="basic-addon2">Browse</span>
              </div>
            </div>
            </div>
            <button type="submit" className="btn btn-success">Success</button>
        </div>
        </div>
        </form>
    );
  }
}

export default AddTask;
