import React from 'react';
import { TasksTypes } from '../types/Task';
import TaskViewer from './TaskViewer';

interface IState {
  taskLists: TasksTypes.TaskDetails[]
};

interface IProps {
  
}


class TasksList extends React.Component<IProps, IState> {

  constructor(props: IProps) {
    super(props);
    this.state = {
      taskLists: []
    };
  };

  reload(event: any) {
    event.preventDefault();

    var request: TasksTypes.Request.GetTaskRequest = {}

    fetch('api/TasksApi/GetTasks', {
      method: 'POST',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(request)
    })
      .then(response => response.json())
      .then((data: any) => {
        debugger;
        this.setState({
          taskLists: data.tasks
        })
      });
  }

  render() {
    return (
      <div className="card">
        <div className="card-header">
          <h1> All tasks</h1>
        </div>
        <div className="card-body">

          <ul>
            {this.state.taskLists.map((value, index) => {
              return <TaskViewer task={value}></TaskViewer>
            })}
          </ul>

          {this.state.taskLists}
        </div>
        <div className="card-footer">
          <button type="button" className="btn btn-success" onClick={this.reload}>Reload</button>

        </div>
      </div>
    );
  }
}

export default TasksList;
