import React from 'react';
import "../Style/AddTask.scss";
import { TasksTypes} from '../types/Task';

interface IState {
  taskContent: string;
};

interface IProps {
}

class AddTask extends React.Component<IProps, IState> {

  constructor(props: IProps) {
    super(props);
    this.state = {
      taskContent: "chagit task - test"
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  };

  handleChange(event: any) {
    this.setState({ taskContent: event.target.value });
  }

  handleSubmit(event: any) {

    event.preventDefault();

    var request: TasksTypes.Request.SaveTaskRequest = {
      TaskDetails: { TaskContent: this.state.taskContent } as TasksTypes.TaskDetails
    }

    fetch('api/TasksApi/SaveTask', {
      method: 'POST',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(request)
    })
      .then(response => response.json())
      .then(data => {

      });
  }


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
      <div className="card">
        <div className="card-header">
          <h1> Add your task</h1>
        </div>
        <div className="card-body">
          <div className="form-group">
            <label>Task</label>
              <div className="input-group mb-3">
                <input type="text" className="form-control" placeholder="Task" value={this.state.taskContent} onChange={this.handleChange} />
            </div>
          </div>
          <div className="form-group">
            <label>Image</label>
              <div className="input-group mb-3">
                <input type="file" accept="image/*" className="form-control image-uploader" placeholder="Image" />
            </div>
            </div>
            <button type="submit" className="btn btn-success">Success</button>
        </div>
        </div>
        </form>
    );
  }
}

export default AddTask;
