import React from 'react';
import { TasksTypes } from '../types/Task';
import TaskViewer from './TaskViewer';

interface IState {
  taskLists: TasksTypes.TaskDetails[]
};

interface IProps {

}


class TasksList extends React.Component<IProps, IState> {

  constructor(props: IProps) {
    super(props);
    this.state = {
      taskLists: []
    };
    var a = [{ TaskContent: "chagittttt" } as TasksTypes.TaskDetails, { TaskContent: "11111111111" } as TasksTypes.TaskDetails, { TaskContent: "22222222" } as TasksTypes.TaskDetails];
    this.setState({ taskLists: a });
  };

  reload(event: any) {
    var data = fetch('api/TasksApi/GetTasks', {
      method: 'GET',
    }).
      then((response) => {
        response.json().then((data: TasksTypes.Response.GetTaskResponse) => {
          console.log(data.tasks);
          this.setState({ taskLists: data.tasks })
      })
    });
  }

  render() {
    return (
      <div className="card">
        <div className="card-header">
          <h1> All tasks</h1>
        </div>
        <div className="card-body">

          {/*<ul>*/}
          {/*  {this.state.taskLists.map((value, index) => {*/}
          {/*    return <TaskViewer task={value}></TaskViewer>*/}
          {/*  })}*/}
          {/*</ul>*/}

          {this.state.taskLists}
        </div>
        <div className="card-footer">
          <button type="button" className="btn btn-success" onClick={this.reload}>Reload</button>

        </div>
      </div>
    );
  }
}

export default TasksList;
