import { read } from 'node:fs';
import React from 'react';
import "../Style/AddTask.scss";
import { TasksTypes } from '../types/Task';

interface IState {
  taskContent: string;
  imageName: string;
  imageData: ArrayBuffer;
};

interface IProps {
}

class AddTask extends React.Component<IProps, IState> {

  constructor(props: IProps) {
    super(props);
    this.state = {
      taskContent: "chagit task - test",
      imageData: '',
      imageName: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  };

  handleChange(event: any) {
    this.setState({  taskContent: event.target.value });
  }

  handleSubmit(event: any) {

    event.preventDefault();

    var request: TasksTypes.Request.SaveTaskRequest = {
      TaskDetails: { TaskContent: this.state.taskContent } as TasksTypes.TaskDetails
    }

    fetch('api/TasksApi/SaveTask', {
      method: 'POST',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(request)
    })
      .then(response => response.json())
      .then(data => {

      });
  }

  imageUploaded(event: any) {
    debugger;
    if (!event.target.files || event.target.files.length == 0) {
      return;
    }
    if (event.target.files.length > 1) {
      return;
    }
    var image = event.target.files[0];

    const reader = new FileReader();
    reader.onload = x => this.setState({ imageData: reader.result });
    if (image) {
      reader.readAsDataURL(image);
    }

  }


  render() {


    return (
      <form onSubmit={this.handleSubmit}>
        <div className="card">
          <div className="card-header">
            <h1> Add your task</h1>
          </div>
          <div className="card-body">
            <div className="form-group">
              <label>Task</label>
              <div className="input-group mb-3">
                <input type="text" className="form-control" placeholder="Task" value={this.state.taskContent} onChange={this.handleChange} />
              </div>
            </div>
            <div className="form-group">
              <label>Image</label>
              <div className="input-group mb-3">
                <input type="text" className="form-control image-uploader pointer" placeholder="Select your image" disabled={true}/>
                <div className="input-group-append" onClick={() => document.getElementById("uploadFile")?.click()}>
                  <input type="file" id="uploadFile" accept="image/*" className="d-none" placeholder="Selsct your image" onChange={this.imageUploaded} />
                  <span className="input-group-text" id="basic-addon2">Browse</span>
                </div>
              </div>
            </div>
            <button type="submit" className="btn btn-success">Success</button>
          </div>
        </div>
      </form>
    );
  }
}

export default AddTask;
